import { useState } from "react"
import { useEffect } from "react"
import { useDispatch, useSelector} from "react-redux"
import { getTypes } from "../../Components/Redux/Actions"
import axios from "axios"



const Form = ()=>{

    const dispatch = useDispatch();
    const tipos = useSelector((state)=>state.type)

    const [form, setForm] = useState({
        Nombre:"",
        Imagen:"",
        Vida:"",
        Ataque:"",
        Defensa:"",
        Velocidad:"",
        Altura:"",
        Peso:"",
        tipo:[]
        
    })



    useEffect(() => {
        dispatch(getTypes())
    }, [dispatch])



    const changeHandler = (event)=>{
        const property = event.target.name;
        const value = event.target.value;

        setForm({...form,[property]:value})

    }
    
    const handlerSubmit = (e) => {
        e.preventDefault()
        axios.post("localhost:3001/pokemons",form)
        .then(res=>alert(res))
    }
    
    const typeSelect = (e) => {
        if (form.tipo.length < 2) {
            setForm({
                ...form,
                tipo: [...form.tipo, e.target.value]
            })
        }
    }

    function resetTipos(e) {
        setForm({
            tipo: []
        })
    }




    return(
        <form onSubmit={handlerSubmit}>
            <div>
                <label>Nombre</label>
                <input 
                    placeholder="Ingresa el nombre de tu Pokemon"
                    name="Nombre"
                    type="text" 
                    valule={form.Nombre} 
                    onChange={changeHandler} 
                />
            </div>

            <div>
                <label>Vida</label>
                <input 
                    placeholder="1-150"
                    name="Vida"
                    type="text"
                    min="0"
                    max="150"
                    valule={form.Vida} 
                    onChange={changeHandler} 
                />
            </div>

            <div>
                <label>Ataque</label>
                <input 
                    name="Ataque"
                    placeholder="1-150"
                    type="text" 
                    min="0"
                    max="150"
                    valule={form.Ataque} 
                    onChange={changeHandler} 
                />
            </div>

            <div>
                <label>Defensa</label>
                <input 
                    name="Defensa"
                    placeholder="1-150"
                    type="text" 
                    min="0"
                    max="150"
                    valule={form.Defensa} 
                    onChange={changeHandler} 
                />
            </div>

            <div>
                <label>Velocidad</label>
                <input 
                    name="Velocidad"
                    placeholder="1-150"
                    min="0"
                    max="150"
                    type="text" 
                    valule={form.Velocidad} 
                    onChange={changeHandler} 
                />
            </div>

            <div>
                <label>Altura</label>
                <input 
                    name="Altura"
                    placeholder="Entre 1 y 10 mts"
                    type="text" 
                    min="0"
                    max="10"
                    valule={form.Altura} 
                    onChange={changeHandler} 
                />
            </div>

            <div>
                <label>Peso</label>
                <input 
                    name="Peso"
                    placeholder="Entre 1 y 100 kgs"
                    type="text"
                    min="0"
                    max="100"
                    valule={form.Peso} 
                    onChange={changeHandler} 
                />
            </div>

            <div>
                <label>Tipo</label>
                <section>               
                    <select onChange={(e) => typeSelect(e)}>
                        {tipos?.map(type => {
                            return (
                               <option key={type.id} value={type.nombre}>{type.nombre}</option>
                        )
                            })}
                    </select>
                    <button onClick={resetTipos} type='button'>Reset Tipos</button>                 
                       
                </section>
                <section>
                          {form.tipo?.map(tipo => {
                            return (
                                <p>{tipo}</p>
                            )
                        })}
                </section>
            </div>

            <div>
                <label>Imagen</label>
                <input 
                    name="Imagen"
                    placeholder="Ingresa la Url de tu imagen"
                    type="text"
                    valule={form.Imagen} 
                    onChange={changeHandler} 
                />
                
            </div>
            
        </form>
    )
}

export default Form;
