import React from "react"
import { useHistory } from "react-router-dom"
import { useState } from "react"
import { useEffect } from "react"
import { useDispatch, useSelector} from "react-redux"
import { getTypes,postPokemon } from "../../Components/Redux/Actions"
import axios from "axios"



const Form = ()=>{

    const dispatch = useDispatch();
    const history = useHistory();
    const tipos = useSelector((state)=>state.types)

    const [form, setForm] = useState({
        nombre:"",
        imagen:"",
        vida:"",
        ataque:"",
        defensa:"",
        velocidad:"",
        altura:"",
        peso:"",
        tipo:[]
        
    })

    const [errors, setErrores] = useState({
        nombre:"",
        imagen:"",
        vida:50,
        ataque:"",
        defensa:"",
        velocidad:"",
        altura:"",
        peso:"",
        tipo:[]
        
    })



    useEffect(() => {
        dispatch(getTypes())
    }, [dispatch])



    const changeHandler = (event)=>{
        const property = event.target.name;
        const value = event.target.value;

        validate({...form,[property]:value})

        setForm({...form,[property]:value})

    }
    
    const handlerSubmit = (e) => {
        e.preventDefault()
        axios.post("http://localhost:3001/pokemons",form)
        .then(res=>alert(res))
    }
    
    const typeSelect = (e) => {
        if (form.tipo.length < 2) {
            setForm({
                ...form,
                tipo: [...form.tipo, e.target.value]
            })
        }
    }

    function resetTipos(e) {
        setForm({
            tipo: []
        })
    }

    function validate(form) {
        if (!form.nombre) {
            setErrores({...errors, nombre:'se requiere ingresar un nombre'})

        } else if (form.nombre.search("[0-9]") !== -1) {
            setErrores({...errors,nombre:"El nombre no debe contener numeros"})

        } else if (parseInt(form.vida) > 150 || parseInt(form.vida) < 1) {
            setErrores({...errors,vida:"El valor de vida debe ser entre 1 y 150"})
            
        } else if (parseInt(form.ataque) > 150 || (parseInt(form.ataque)) < 1) {
            setErrores({...errors, ataque:"El ataque debe ser entre 1 y 1500"}) 

        } else if (parseInt(form.defensa) > 150 || (parseInt(form.defensa)) < 1) {
            setErrores({...errors, defensa:"La defensa debe ser entre 1 y 150"})

        }else if (form.tipo.length > 2) {
            setErrores({...errors, tipo:"solo puede seleccionar 2 tipos"})

        } else if (parseInt(form.altura) > 10 || (parseInt(form.altura)) < 1) {
            setErrores({...errors, altura:"La Altura debe ser entre 1 y 10 mts"})

        } else if (parseInt(form.peso) > 100 || (parseInt(form.peso)) < 1) {
            setErrores({...errors, peso:'La defensa debe ser entre 1 y 100 Kgs.'}) 
        }};  




    return(
        <form onSubmit={handlerSubmit}>
            <div>
                <label>Nombre: </label>
                <input 
                    placeholder="Nombre del Pokemon"
                    name="nombre"
                    key="nombre"
                    type="text" 
                    valule={form.nombre} 
                    onChange={changeHandler}     
                />
                <span>{errors.nombre}</span>
                
            </div>

            <div>
                <label>Vida: </label>
                <input 
                    placeholder="1-150"
                    name="vida"
                    key="vida"
                    type="text"
                    min="0"
                    max="150"
                    valule={form.vida} 
                    onChange={changeHandler} 
                />
                <span>{errors.vida}</span>
                
            </div>

            <div>
                <label>Ataque: </label>
                <input 
                    name="ataque"
                    key="ataque"
                    placeholder="1-150"
                    type="text" 
                    min="0"
                    max="150"
                    valule={form.ataque} 
                    onChange={changeHandler} 
                />
                <span>{errors.ataque}</span>
            </div>

            <div>
                <label>Defensa: </label>
                <input 
                    name="defensa"
                    key="defensa"
                    placeholder="1-150"
                    type="text" 
                    min="0"
                    max="150"
                    valule={form.defensa} 
                    onChange={changeHandler} 
                />
                <span>{errors.defensa}</span>
            </div>

            <div>
                <label>Velocidad: </label>
                <input 
                    name="velocidad"
                    key="velocidad"
                    placeholder="1-150"
                    min="0"
                    max="150"
                    type="text" 
                    valule={form.velocidad} 
                    onChange={changeHandler} 
                />
                <span>{errors.velocidad}</span>
            </div>

            <div>
                <label>Altura: </label>
                <input 
                    name="altura"
                    key="altura"
                    placeholder="Entre 1 y 10 mts"
                    type="text" 
                    min="0"
                    max="10"
                    valule={form.altura} 
                    onChange={changeHandler} 
                />
                <span>{errors.altura}</span>
            </div>

            <div>
                <label>Peso: </label>
                <input 
                    name="peso"
                    key="peso"
                    placeholder="Entre 1 y 100 kgs"
                    type="text"
                    min="0"
                    max="100"
                    valule={form.peso} 
                    onChange={changeHandler} 
                />
                <span>{errors.peso}</span>
            </div>

            <div>
                <label>Tipo: <select onChange={(e) => typeSelect(e)}>
                        {tipos?.map(types => {
                            return (
                               <option value={types.nombre}>{types.nombre}</option>
                        )
                            })}
                    </select></label>
                <section>               
                    
                    <button onClick={resetTipos} type='button'>Reset Tipos</button>                 
                       
                </section>
                <section>
                          {form.tipo?.map(tipo => {
                            return (
                                <p>{tipo}</p>
                            )
                        })}
                </section>
                <span>{errors.tipo}</span>
            </div>

            <div>
                <label>Imagen</label>
                <input 
                    name="Imagen"
                    placeholder="Ingresa la Url de tu imagen"
                    type="text"
                    valule={form.imagen} 
                    onChange={changeHandler} 
                />
                
            </div>
            <button type="submit">Crear Pokemon</button>
            
        </form>
    )
}

export default Form;
