import { GET_POKEMONS, GET_TYPES,FILTER_POKEMONS_TYPE,FILTER_FROM} from "./Actions";

const initialState = {
    pokemons: [],
    allpokemons: [],
    types: [],
    detail: [],
}


const rootReducer = (state=initialState, action)=>{
    switch(action.type){
        case GET_POKEMONS:
            return {
                ...state,
                pokemons: action.payload,
                allpokemons:action.payload,
                detail:[]
            }
        case GET_TYPES:
            return {
                ...state,
                types: action.payload,
            }

        case FILTER_POKEMONS_TYPE:
            const pokeTotal = state.allpokemons;
            const pokemonesfilter =
                action.payload === "Todos"
                ? pokeTotal
                : pokeTotal.filter((poke) => poke.tipo.includes(action.payload));
            return {
                ...state,
                pokemons: pokemonesfilter,
            }

        case FILTER_FROM:
            if (action.payload === "todos")
                return {
                ...state,
                pokemons: state.allpokemons,
                };
            const pokeFiltrados =
                action.payload === "creados"
                ? state.allpokemons.filter((poke) => poke.creadoDb === true)
                : state.allpokemons.filter((poke) => !poke.creadoDb);
            return {
                ...state,
                pokemons: pokeFiltrados,
            };
        default:
            return{...state}
    }

}

export default rootReducer;
