import { useSelector } from "react-redux";

const DetailedPokemon = () => {
  const selectorID = useSelector((state) => state.pokemons);

  return (
    <div>
        
        {selectorID.length >= 1 ? 

        <div>

            <img src={selectorID.imagen} alt="Imagen pokemon" />

            <div>                    
                    <h5>Nombre : {selectorID.nombre.charAt(0).toUpperCase() + selectorID.nombre.substring(1)}</h5>
                    <h5> ID: {selectorID.id}</h5>
                    <h5>Tipo : {selectorID.tipo.map(t => t.charAt(0).toUpperCase() + t.substring(1)).join(' - ')}</h5>
                
                <div>
                     <h5>Vida:{selectorID.vida}</h5>
                     <h5>Defensa: {selectorID.defensa}</h5>
                     <h5>Ataque: {selectorID.ataque}</h5>
                </div>
                <div>
                     <h5>Velocidad:{selectorID.velocidad}</h5>
                     <h5>Altura: {selectorID.altura}</h5>
                     <h5>Peso: {selectorID.peso}</h5>
                </div>
            </div>
            
        </div> 
        
        
        : <div>
            <img  alt="Tu pokemon no quiere mostrarse" />
        </div>
        }

    </div>
  );
};

export default DetailedPokemon;
