import { useState } from "react"
import { useEffect } from "react"
import { useDispatch, useSelector} from "react-redux"
import { getTypes } from "../../Components/Redux/Actions"
import axios from "axios"



const Form = ()=>{

    const dispatch = useDispatch();
    const tipos = useSelector((state)=>state.types)

    const [form, setForm] = useState({
        Nombre:"",
        Imagen:"",
        Vida:"",
        Ataque:"",
        Defensa:"",
        Velocidad:"",
        Altura:"",
        Peso:"",
        tipo:[]
        
    })

    const [errors, set] = useState({
        Nombre:"",
        Imagen:"",
        Vida:"",
        Ataque:"",
        Defensa:"",
        Velocidad:"",
        Altura:"",
        Peso:"",
        tipo:[]
        
    })



    useEffect(() => {
        dispatch(getTypes())
    }, [dispatch])



    const changeHandler = (event)=>{
        const property = event.target.name;
        const value = event.target.value;

        validate({...form,[property]:value})

        setForm({...form,[property]:value})

    }
    
    const handlerSubmit = (e) => {
        e.preventDefault()
        axios.post("localhost:3001/pokemons",form)
        .then(res=>alert(res))
    }
    
    const typeSelect = (e) => {
        if (form.tipo.length < 2) {
            setForm({
                ...form,
                tipo: [...form.tipo, e.target.value]
            })
        }
    }

    function resetTipos(e) {
        setForm({
            tipo: []
        })
    }

    function validate(form) {
        const errores = {}
        if (!form.Nombre) {
            errores.Nombre = 'se requiere ingresar un nombre'

        } else if (form.Nombre.search("[0-9]") !== -1) {
            errores.Nombre = "El nombre no debe contener numeros";

        } else if (parseInt(form.Vida) > 150 || parseInt(form.Vida) < 1) {
            errores.Vida = 'el valor de vida debe ser entre 1 y 150'

        } else if (parseInt(form.Ataque) > 150 || (parseInt(form.Ataque)) < 1) {
            errores.Ataque = 'El ataque debe ser entre 1 y 1500'

        } else if (parseInt(form.Defensa) > 150 || (parseInt(form.Defensa)) < 1) {
            errores.Defensa = 'La defensa debe ser entre 1 y 150'

        } else if (form.tipo.length > 2) {
            errores.tipo = 'solo puede seleccionar 2 tipos'

        } else if (parseInt(form.Altura) > 10 || (parseInt(form.Altura)) < 1) {
            errores.Altura = 'La Altura debe ser entre 1 y 10 mts'

        } else if (parseInt(form.Peso) > 100 || (parseInt(form.Peso)) < 1) {
            errores.Peso = 'La defensa debe ser entre 1 y 100 Kgs.'
        } 
        return errores;
    }




    return(
        <form onSubmit={handlerSubmit}>
            <div>
                <label>Nombre: </label>
                <input 
                    placeholder="Nombre del Pokemon"
                    name="Nombre"
                    type="text" 
                    valule={form.Nombre} 
                    onChange={changeHandler}     
                />
                {errores.Nombre }
                
            </div>

            <div>
                <label>Vida: </label>
                <input 
                    placeholder="1-150"
                    name="Vida"
                    type="text"
                    min="0"
                    max="150"
                    valule={form.Vida} 
                    onChange={changeHandler} 
                />
            </div>

            <div>
                <label>Ataque: </label>
                <input 
                    name="Ataque"
                    placeholder="1-150"
                    type="text" 
                    min="0"
                    max="150"
                    valule={form.Ataque} 
                    onChange={changeHandler} 
                />
            </div>

            <div>
                <label>Defensa: </label>
                <input 
                    name="Defensa"
                    placeholder="1-150"
                    type="text" 
                    min="0"
                    max="150"
                    valule={form.Defensa} 
                    onChange={changeHandler} 
                />
            </div>

            <div>
                <label>Velocidad: </label>
                <input 
                    name="Velocidad"
                    placeholder="1-150"
                    min="0"
                    max="150"
                    type="text" 
                    valule={form.Velocidad} 
                    onChange={changeHandler} 
                />
            </div>

            <div>
                <label>Altura: </label>
                <input 
                    name="Altura"
                    placeholder="Entre 1 y 10 mts"
                    type="text" 
                    min="0"
                    max="10"
                    valule={form.Altura} 
                    onChange={changeHandler} 
                />
            </div>

            <div>
                <label>Peso: </label>
                <input 
                    name="Peso"
                    placeholder="Entre 1 y 100 kgs"
                    type="text"
                    min="0"
                    max="100"
                    valule={form.Peso} 
                    onChange={changeHandler} 
                />
            </div>

            <div>
                <label>Tipo: <select onChange={(e) => typeSelect(e)}>
                        {tipos?.map(types => {
                            return (
                               <option value={types.nombre}>{types.nombre}</option>
                        )
                            })}
                    </select></label>
                <section>               
                    
                    <button onClick={resetTipos} type='button'>Reset Tipos</button>                 
                       
                </section>
                <section>
                          {form.tipo?.map(tipo => {
                            return (
                                <p>{tipo}</p>
                            )
                        })}
                </section>
            </div>

            <div>
                <label>Imagen</label>
                <input 
                    name="Imagen"
                    placeholder="Ingresa la Url de tu imagen"
                    type="text"
                    valule={form.Imagen} 
                    onChange={changeHandler} 
                />
                
            </div>
            
        </form>
    )
}

export default Form;
