import axios from "axios";

export const GET_POKEMONS = "GET_POKEMONS";
export const GET_TYPES = "GET_TYPES"
export const FILTER_POKEMONS_TYPE = "FILTER_POKEMONS_TYPE"
export const FILTER_FROM = "FILTER_FROM"
export const ORDER_NAME = "ORDER_NAME"
export const ORDEN_ATAQUE = "ORDEN_ATAQUE"
export const GET_POKEMON_NAME = "GET_POKEMON_NAME"


export const getPokemons = ()=>{
    return async function(dispatch) {
        const apiData = await axios.get("/pokemons")
        const pokemons = apiData.data;
    dispatch({
        type: GET_POKEMONS, 
        payload: pokemons,
        detail:[]
    })}
};

export function getTypes() {
    return async function (dispatch) {
      let types = await axios.get("/types", {});
  
      return dispatch({
        type: GET_TYPES,
        payload: types.data,
      });
    };
};

export function filterForType(payload) {
  return {
    type: FILTER_POKEMONS_TYPE,
    payload: payload,
  };
};

export function filterFrom(payload) {
  return {
    type: FILTER_FROM,
    payload: payload,
  };
};

export function orderName(payload) {
  return {
    type: ORDER_NAME,
    payload: payload,
  };
}

export function ordenAtaque(payload){
  return {
    type: ORDEN_ATAQUE,
    payload:payload
  }
}


export function getPokeName(nombre) {
  return async function (dispatch) {
    try {
      const pokeName = await axios.get(
        `/pokemons?nombre=${nombre}`
      );
      return dispatch({
        type: GET_POKEMON_NAME,
        payload: pokeName.data,
      });
    } catch (error) {
      console.log(error);
    }
  };
}

export function postPokemon(pokemon) {
  try {
    return function (dispatch) {
      return axios.post("/pokemons", pokemon);
    };

  } catch (error) {
  }
}

export function getPokeId(idPokemon) {
  return async function (dispatch) {
    try {
      const pokeId = await axios.get(`/pokemons/`+ idPokemon);

      return dispatch({
        type: "GET_POKEMON_ID",
        payload: pokeId.data,
      });
    } catch (error) {
      console.log('error');
    }
  };
}

export function deletePokemon(idPokemon){
  return async function(dispatch){
     try {
    return axios.delete("/pokemons/"+idPokemon)
  } catch (error) {
    console.log(error)
  }
  } 
}

export function putPokemon(pokemon) {
  const {id} = pokemon
  try {
    return function (dispatch) {
      return axios.put(`/pokemons/${id}`, pokemon);
    };
    
  } catch (error) {
    console.log(error);
    return `Algo salio mal, intente de nuevo: ${error}`;
  }
}
