import Style from "./cardscontainer.module.css"
import Card from "../Card/card"
import { useSelector } from "react-redux"
import { useState } from "react"
import next from "../IMG/next.png"
import prev from "../IMG/prev.png"
import {Link} from "react-router-dom"

const CardsContainer = ()=>{
 
  const pokemons = useSelector(state=>state.pokemons)

  const [page, setPage] = useState(0);

  const pagination = () => {
    if (pokemons.length) return pokemons.slice(page, page + 12);
    if (pokemons.info) return pokemons;
    return [];
  };
  
  const array = pagination();
  

  const nextPage = () => {
    if (pokemons.length > page + 12) {
      setPage(page + 12);
    }
  };

  const previusPage = () => {
    if (page > 0) {
      setPage(page - 12);
    }
  };

    return(
    <div>
      <div className={s.div_select}>
                <select onChange={(e) => handleOrder(e)}>
                    <option value='nombreAz'>Nombre A - Z</option>
                    <option value='nombreZa'>Nombre Z - A</option>
                </select>

                <select onChange={(e) => handleOrderAtaque(e)}>
                    <option value='ataqueAz'>Ataque A - Z</option>
                    <option value='ataqueZa'>Ataque Z - A</option>
                </select>

                <select onChange={(e) => handleFilterType(e)}>
                    <option key={allTypes.length + 1} value='Todos'>Todos los tipos</option>
                    {allTypes?.map(type => {
                        return (<option key={type.id} value={type.nombre}>{type.nombre}</option>)
                    })}
                </select>

                <select onChange={(e) => handleFilterCreated(e)}>
                    <option value='todos'>Todos los creados</option>
                    <option value='creados'>Creados en DB</option>
                    <option value='api'>Poke API</option>
                </select>

            </div>  



      <div>
        <a onClick={previusPage}><img class={Style.prev} src={prev} alt="Previous" ></img></a>
        <a>{(page + 12)/12}</a>
        <a onClick={nextPage}><img class={Style.next} src={next} alt="Next"></img></a>
        <p></p>
      </div>
        <div class={Style.container}>
          {array.map((pokemon)=>{
            return(
            <Link key={pokemon.id} to={`/detail/${pokemon.id}`}>
              <div>
                <Card
                  id={pokemon.id}
                  nombre={pokemon.nombre}
                  imagen={pokemon.imagen}
                  tipo={pokemon.tipo}
                />
                </div>
            </Link>)
          })}
        </div> 
    </div>
    )
}

export default CardsContainer;
