import React from "react"
import { useHistory } from "react-router-dom"
import { useState } from "react"
import { useEffect } from "react"
import { useDispatch, useSelector} from "react-redux"
import { getTypes,postPokemon } from "../../Components/Redux/Actions"




const Form = ()=>{

    const dispatch = useDispatch();
    const history = useHistory();
    const tipos = useSelector((state)=>state.types)

    const [form, setForm] = useState({
        nombre:"",
        imagen:"",
        vida:"",
        ataque:"",
        defensa:"",
        velocidad:"",
        altura:"",
        peso:"",
        tipo:[]
        
    })

    const [errors, setErrores] = useState({
        nombre:"",
        imagen:"",
        vida:"",
        ataque:"",
        defensa:"",
        velocidad:"",
        altura:"",
        peso:"",
        tipo:[]
        
    })



    useEffect(() => {
        dispatch(getTypes())
    }, [dispatch])



    const changeHandler = (event) => {
        const property = event.target.name;
        const value = event.target.value;
        const newErrors = validate({ ...form, [property]: value });
        setErrores(newErrors);
        setForm({ ...form, [property]: value });
    };
    
    const handlerSubmit = (e) => {
        e.preventDefault()
        if ( !form.nombre){
            return alert ("No se puede crear un pokemon sin nombre")
        }

        dispatch(postPokemon(form))
        setForm({
            nombre: "",
            vida: 50,
            ataque: 50,
            defensa: 50,
            velocidad: 50,
            altura: 50,
            peso: 50,
            imagen:"",
            tipo: []
        })
        history.push('/home')
        
    }
    
    const typeSelect = (e) => {
        if (form.tipo.length < 2) {
            setForm({
                ...form,
                tipo: [...form.tipo, e.target.value]
            })
        }
    }

    function resetTipos(e) {
        setForm({
            tipo: []
        })
    }

    function validate(form) {
        const newErrors = {
          nombre: "",
          imagen: "",
          vida: "",
          ataque: "",
          defensa: "",
          velocidad: "",
          altura: "",
          peso: "",
          tipo: [],
        };

        if (!form.nombre) {newErrors.nombre = "Se requiere ingresar un nombre";} 
        if (form.nombre.search("[0-9]") !== -1) {newErrors.nombre = "El nombre no debe contener números";}
        if (parseInt(form.vida) > 150 || parseInt(form.vida) < 1) {newErrors.vida = "El valor de vida debe ser entre 1 y 150";}
        if (parseInt(form.ataque) > 150 || parseInt(form.ataque) < 1) {newErrors.ataque = "El ataque debe ser entre 1 y 150";}
        if (parseInt(form.defensa) > 150 || parseInt(form.defensa) < 1) {newErrors.defensa = "La defensa debe ser entre 1 y 150";
    }

    if (form.tipo.length > 2) {
      newErrors.tipo = "Solo puede seleccionar 2 tipos";
    }

    if (parseInt(form.altura) > 10 || parseInt(form.altura) < 1) {
      newErrors.altura = "La Altura debe ser entre 1 y 10 mts";
    }

    if (parseInt(form.peso) > 100 || parseInt(form.peso) < 1) {
      newErrors.peso = "El peso debe ser entre 1 y 100 Kgs.";
    }

    return newErrors;
  }



    return(
        <form onSubmit={(e)=> handlerSubmit(e)}>
            <div>
                <label>Nombre: </label>
                <input 
                    placeholder="Nombre del Pokemon"
                    name="nombre"
                    key="nombre"
                    type="text" 
                    valule={form.nombre} 
                    onChange={(e) => changeHandler(e)}     
                />
                
                
            </div>
            {errors.nombre && <span>{errors.nombre}</span>}

            <div>
                <label>Vida: </label>
                <input 
                    placeholder="1-150"
                    name="vida"
                    key="vida"
                    type="text"
                    min="0"
                    max="150"
                    valule={form.vida} 
                    onChange={changeHandler} 
                />
               {errors.vida &&<span>{errors.vida}</span>}
                
            </div>

            <div>
                <label>Ataque: </label>
                <input 
                    name="ataque"
                    key="ataque"
                    placeholder="1-150"
                    type="text" 
                    min="0"
                    max="150"
                    valule={form.ataque} 
                    onChange={changeHandler} 
                />
                {errors.ataque && <span>{errors.ataque}</span>}
            </div>

            <div>
                <label>Defensa: </label>
                <input 
                    name="defensa"
                    key="defensa"
                    placeholder="1-150"
                    type="text" 
                    min="0"
                    max="150"
                    valule={form.defensa} 
                    onChange={changeHandler} 
                />
                {errors.defensa && <span>{errors.defensa}</span>}
            </div>

            <div>
                <label>Velocidad: </label>
                <input 
                    name="velocidad"
                    key="velocidad"
                    placeholder="1-150"
                    min="0"
                    max="150"
                    type="text" 
                    valule={form.velocidad} 
                    onChange={changeHandler} 
                />
                {errors.velocidad && <span>{errors.velocidad}</span>}
            </div>

            <div>
                <label>Altura: </label>
                <input 
                    name="altura"
                    key="altura"
                    placeholder="Entre 1 y 10 mts"
                    type="text" 
                    min="0"
                    max="10"
                    valule={form.altura} 
                    onChange={changeHandler} 
                />
                {errors.altura && <span>{errors.altura}</span>}
            </div>

            <div>
                <label>Peso: </label>
                <input 
                    name="peso"
                    key="peso"
                    placeholder="Entre 1 y 100 kgs"
                    type="text"
                    min="0"
                    max="100"
                    valule={form.peso} 
                    onChange={changeHandler} 
                />
                {errors.peso && <span>{errors.peso}</span>}
            </div>

            <div>
                <label>Tipo: <select onChange={(e) => typeSelect(e)}>
                        {tipos?.map(type => {
                            return (
                               <option key={type.id} value={type.nombre}>{type.nombre}</option>
                        )
                            })}
                    </select></label>
                <section>               
                    
                    <button onClick={resetTipos} type='button'>Reset Tipos</button>                 
                       
                </section>
                <section>
                          {form.tipo?.map(tipo => {
                            return (
                                <p>{tipo}</p>
                            )
                        })}
                </section>
                <span>{errors.tipo}</span>
            </div>

            <div>
                <label>Imagen</label>
                <input 
                    name="imagen"
                    key="imagen"
                    placeholder="Ingresa la Url de tu imagen"
                    type="text"
                    valule={form.imagen} 
                    onChange={(e) => changeHandler(e)} 
                />
                
            </div>
            <button type="submit">Crear Pokemon</button>
            
        </form>
    )
}

export default Form;
