import React from "react"
import {useDispatch, useSelector} from "react-redux"
import { getPokeId } from "../../Components/Redux/Actions"
import { useEffect } from "react"


export default function Detail(props) {
    
    const dispatch = useDispatch()
    const idPokemon = props.match.params.id
    {console.log(idPokemon)}

    useEffect(() => {
        dispatch(getPokeId(idPokemon))
    }, [dispatch])

    const pokemonID = useSelector((state) => state.detail)

    //para borrar el estado previo.
    //¿agregar un evento al boton para que despache una action y limpie el state.deatail?

    // const handleLimpiaDetalle = (e)=>{
    //     dispatch(limpiarDetalle(e))
    // }

    return (
        <div >
            <div>
            </div>
            {pokemonID.length >= 1 ? 
            
            <div>
                <img  src={pokemonID[0].imagen} alt="Imagen pokemon" />

                <div>                    
                        <h5 >Nombre : {pokemonID[0].nombre.charAt(0).toUpperCase() + pokemonID[0].nombre.substring(1)}</h5>
                        <h5 > ID : {pokemonID[0].id}</h5>
                        <h5 >Tipo : {pokemonID[0].tipo.map(t => t.charAt(0).toUpperCase() + t.substring(1)).join(' - ')}</h5>
                    
                    <div>
                         <h5>Vida: {pokemonID[0].vida}</h5>
                         <h5>Defensa: {pokemonID[0].defensa}</h5>
                         <h5>Ataque: {pokemonID[0].ataque}</h5>
                    </div>
                    <div>
                         <h5>Velocidad: {pokemonID[0].velocidad}</h5>
                         <h5>Altura: {pokemonID[0].altura}</h5>
                         <h5>Peso: {pokemonID[0].peso}</h5>
                    </div>
                </div>
                
            </div> 
            
            
            : <div >Tupokemon no quiere mostrarse</div>
            }

        </div>
    )


}
