import { GET_POKEMONS, GET_TYPES,F} from "./Actions";

const initialState = {
    pokemons: [],
    allpokemons: [],
    types: [],
    detail: [],
}


const rootReducer = (state=initialState, action)=>{
    switch(action.type){
        case GET_POKEMONS:
            return {
                ...state,
                pokemons: action.payload,
                allpokemons:action.payload,
                detail:[]
            }
        case GET_TYPES:
            return {
                ...state,
                types: action.payload,
            }

        case FILTER_POKEMONS_TYPE:
            const pokeTotal = state.pokeonesFull;
            const pokemonesfilter =
                action.payload === "Todos"
                ? pokeTotal
                : pokeTotal.filter((poke) => poke.tipo.includes(action.payload));
            return {
                ...state,
                pokemons: pokemonesfilter,
            }

        default:
            return{...state}
    }

}

export default rootReducer;
