import { GET_POKEMONS } from "./Actions";

const initialState = {
    pokemons: [],
    allpokemons: [],
    types: [],
    detail: [],
}


const rootReducer = (state=initialState, action)=>{
    switch(action.type){
        case GET_POKEMONS:
            return {
                ...state,
                pokemons: action.payload,
                allpokemons:action.payload,
                detail:[]
            }
        case "GET_TYPES":
                return {
                  ...state,
                  types: action.payload,
                };
        default:
            return{...state}
    }

}

export default rootReducer;
