import { useSelector } from "react-redux";

const DetailedPokemon = () => {
  const selectorID = useSelector((state) => state.pokemons);

  return (
    <div>


        <div>

            <img src={selectorID.imagen} alt="Imagen pokemon" />

            <div>                    
                    <h5>Nombre : {selectorID[0].nombre.charAt(0).toUpperCase() + selectorID[0].nombre.substring(1)}</h5>
                    <h5> ID: {selectorID[0].id}</h5>
                    <h5>Tipo : {selectorID[0].tipo.map(t => t.charAt(0).toUpperCase() + t.substring(1)).join(' - ')}</h5>
                
                <div>
                     <h5>Vida:{selectorID[0].vida}</h5>
                     <h5>Defensa: {selectorID[0].defensa}</h5>
                     <h5>Ataque: {selectorID[0].ataque}</h5>
                </div>
                <div>
                     <h5>Velocidad:{selectorID[0].velocidad}</h5>
                     <h5>Altura: {selectorID[0].altura}</h5>
                     <h5>Peso: {selectorID[0].peso}</h5>
                </div>
            </div>
            
        </div> 
        
        
        : <div>
            <img  alt="Tu pokemon no quiere mostrarse" />
        </div>
        }

    </div>
  );
};

export default DetailedPokemon;
